import React from "react";
import PropTypes from "prop-types";
import styled from "styled-components";
import Loader from "Components/Loader";
import Helmet from "react-helmet";
import Season from "Components/Season";

const Container = styled.div`
	height: calc(100vh - 50px);
	width: 100%;
	position: relative;
	padding: 50px;
`;

const Content = styled.div`
	display: flex;
	width: 100%;
	height: 100%;
	position: relative;
	z-index: 1;
`;

const Cover = styled.div`
	width: 30%;
	background-image: url(${(props) => props.bgImage});
	background-position: center center;
	background-size: cover;
	border-radius: 5px;
`;

const Data = styled.div`
	width: 40%;
	margin-left: 25px;
`;

const Title = styled.h3`
	font-size: 32px;
	margin-bottom: 20px;
`;

const ItemContainer = styled.div`
	margin: 10px 0;
`;

const Item = styled.span`
	margin-bottom: 10px;
`;

const Imdb = styled.a`
	margin-top: 20px;
`;

const ImdbLogo = styled.img`
	border: 1px solid #000;
	border-radius: 3px;
	margin-bottom: 2px;
`;

const Divider = styled.span`
	margin: 0 10px;
`;

const Overview = styled.p`
	font-size: 20px;
	line-height: 1.2;
	width: 100%;
	margin-top: 10px;
`;

const Backdrop = styled.div`
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background-image: url(${(props) => props.bgImage});
	background-position: center center;
	background-size: cover;
	filter: blur(2px);
	opacity: 0.5;
	z-index: 0;
`;

const Box = styled.ul`
	width: 100%;
	margin-top: 2px;
	display: flex;
`;

const Info = styled.li`
	list-style: none;
	font-size: 12px;
	padding-right: 5px;
`;

const DetailPresenter = ({ result, externalId, loading, error }) =>
	loading ? (
		<>
			<Helmet>
				<title>Loading | Wonfix</title>
			</Helmet>
			<Loader />
		</>
	) : (
		<>
			<Container>
				<Helmet>
					<title>
						{result.original_title
							? result.original_title
							: result.original_name}{" "}
						| Wonflix
					</title>
				</Helmet>
				<Backdrop
					bgImage={`https://image.tmdb.org/t/p/original${result.backdrop_path}`}
				/>
				<Content>
					<Cover
						bgImage={
							result.poster_path
								? `https://image.tmdb.org/t/p/original${result.poster_path}`
								: require("../../Components/assets/noPosterSmall.png").default
						}
					/>
					<Data>
						<Title>
							{result.original_title
								? result.original_title
								: result.original_name}
						</Title>
						<ItemContainer>
							<Item>
								{result.release_date
									? result.release_date.substring(0, 4)
									: result.first_air_date.substring(0, 4)}
							</Item>
							<Divider>.</Divider>
							<Item>
								{result.runtime ? result.runtime : result.episode_run_time[0]}
								min
							</Item>
							<Divider>.</Divider>
							<Item>
								{result.genres &&
									result.genres.map((genre, index) =>
										index === result.genres.length - 1
											? genre.name
											: `${genre.name}/`
									)}
							</Item>
						</ItemContainer>
						<Imdb
							href={`https://imdb.com/title/${
								result.imdb_id ? result.imdb_id : externalId.imdb_id
							}`}
						>
							<ImdbLogo
								src="data:image/png;base64,"
								alt="imdbImage"
								width="40"
								height="15"
							/>
						</Imdb>
						<Box>
							<Info>
								{result.production_companies &&
									result.production_companies.map(
										(production_companies, index) =>
											index === result.production_companies.length - 1
												? ` ${production_companies.name}`
												: `${production_companies.name} / `
									)}
							</Info>
							<Info>
								{result.production_countries &&
									result.production_countries.map(
										(production_countries, index) =>
											index === result.production_countries.length - 1
												? `Made in ${production_countries.name} `
												: `Made in ${production_countries.name} / `
									)}
							</Info>
						</Box>
						<Overview>{result.overview}</Overview>
					</Data>
					<Season>
						{result.seasons &&
							result.seasons.map((seasons) => (
								<Season
									key={seasons.id}
									id={seasons.id}
									imageUrl={seasons.poster_path}
									name={seasons.name}
								/>
							))}
					</Season>
				</Content>
			</Container>
		</>
	);

DetailPresenter.propTypes = {
	result: PropTypes.object.isRequired,
	loading: PropTypes.bool.isRequired,
	error: PropTypes.string,
};

export default DetailPresenter;
